import{_ as a,o as t,c as e,S as r}from"./chunks/framework.f62eb0f6.js";const g=JSON.parse('{"title":"阮一峰-科技爱好者周刊","description":"","frontmatter":{"title":"阮一峰-科技爱好者周刊"},"headers":[],"relativePath":"read/ruanyifeng.md","filePath":"read/ruanyifeng.md","lastUpdated":1694416836000}'),i={name:"read/ruanyifeng.md"},l=r('<h1 id="阮一峰-科技爱好者周刊" tabindex="-1">阮一峰-科技爱好者周刊 <a class="header-anchor" href="#阮一峰-科技爱好者周刊" aria-label="Permalink to &quot;阮一峰-科技爱好者周刊&quot;">​</a></h1><h2 id="视频学习胜过读书吗" tabindex="-1">视频学习胜过读书吗？ <a class="header-anchor" href="#视频学习胜过读书吗" aria-label="Permalink to &quot;视频学习胜过读书吗？&quot;">​</a></h2><ul><li>人类学习效率最高的方式，不是<strong>读书 + 思考</strong>，而是<strong>观察 + 模仿</strong>。前者需要较长时间的注意力投入，后者只需要很短时间注意力，更符合人类的天性。</li><li><strong>大部分领域的入门方式，都是观察和模仿，所以视频学习更有效。</strong> 对于深入学习，文字和书籍的效果可能要好于视频，因为入门以后再往上走，<strong>理解 + 思考</strong>的作用越来越大，<strong>观察 + 模仿</strong>的作用越来越小。但是，大多数人需要的只是入门，成为专业工作者毕竟是少数。</li><li>视频学习才是初学者更好的方式，或者说，视频是大众学习的主流，读书更适用于某个主题的进阶学习。</li></ul><h2 id="程序员需要担心裁员吗" tabindex="-1">程序员需要担心裁员吗？ <a class="header-anchor" href="#程序员需要担心裁员吗" aria-label="Permalink to &quot;程序员需要担心裁员吗？&quot;">​</a></h2><p>程序员不需要过分担心裁员的原因</p><ul><li>称职的程序员很难招聘</li><li>已经上线的系统需要维护。如果你参与线上系统，除非公司打算放弃该系统，否则不太会考虑解雇你。</li><li>你的经验，让你在市场上变的抢手。</li><li>很多公司不是真的要裁员，而是在调整雇员结构。</li><li>如果你不喜欢现在的工作，被解雇其实是一件好事，让你有机会去找一份让自己兴奋的新工作。</li></ul><p>为了预防万一，你可以做两方面的准备</p><ul><li>预留一些现金，至少能够应付三个月的支出。</li><li>强化一些方便求职的技能。</li></ul><p>不过，这两件事是平时就应该做到的，不管你会不会遇到裁员。</p><p><strong>与其担心宏观经济，不如关注自己。你对自己生活的影响，往往比宏观经济对你的影响大得多。</strong></p>',10),n=[l];function o(s,d,_,c,h,u){return t(),e("div",null,n)}const f=a(i,[["render",o]]);export{g as __pageData,f as default};
